import { useState } from 'react';
import './App.css';
import Button from "./components/Button";
import Input from "./components/Input";

const App = () => {

  const [result, setResult] = useState("");
  const [digit, setDigit]= useState("");
  const btnColor = "#f2a33c";

  const addDigit = (val) => {
    setDigit((digit) => [...digit, val + ""]);
  };

  return (
    <div className="App">
     <div className='container'>
       <Input digit = {digit} result= {result}/>
       <div className='row'>
         <Button value="A"/>
         <Button value="-/+"/>
         <Button value="%"/>
         <Button value="/" color={btnColor}/>
       </div>
       <div className='row'>
         <Button value="7" doTask={addDigit}/>
         <Button value="8"/>
         <Button value="9"/>
         <Button value="X" color={btnColor}/>
       </div>
       <div className='row'>
         <Button value="4"/>
         <Button value="5"/>
         <Button value="6"/>
         <Button value="_" color={btnColor}/>
       </div>
       <div className='row'>
         <Button value="1"/>
         <Button value="2"/>
         <Button value="3"/>
         <Button value="+" color={btnColor}/>
       </div>
       <div className='row'>
         <Button value="&nbsp;&nbsp;&nbsp;&nbsp;0"/>
         <Button value="" disabled/>
         <Button value=","/>
         <Button value="=" color={btnColor}/>
       </div>
     </div>
    </div>
  );
}

export default App;
